require_once dirname(__FILE__).'/config.php'; // DPS2_PLUGIN_DISPLAYNAME
require_once BASEDIR.'/server/interfaces/plugins/connectors/WebApps_EnterpriseConnector.class.php';

class AdobeDps2_WebApps extends WebApps_EnterpriseConnector
{
	final public function getWebApps()
	{
		$apps = array();
		
		$importApp = new WebAppDefinition();
		$importApp->IconUrl = 'data:image/svg+xml;base64,';
		$importApp->IconCaption = DPS2_PLUGIN_DISPLAYNAME;
		$importApp->WebAppId  = 'Admin';
		$importApp->ShowWhenUnplugged = false;
		$apps[] = $importApp;
		
		return $apps;
	}
}
